#!/usr/bin/env python3 import json
def fix_json(input_file, output_file): print("Reading input file...") with open(input_file, 'r', encoding='utf-8') as f: content = f.read()
print("Fixing JSON formatting...") # Fix common JSON issues content = content.replace("'", '"') # Replace single quotes with double quotes content = content.replace(',]', ']') # Remove trailing commas before ] content = content.replace(',}', '}') # Remove trailing commas before }
# Make sure the content is wrapped in [] if it's not already content = content.strip() if not content.startswith('['): content = '[' + content if not content.endswith(']'): content = content + ']'
print("Validating JSON...") try: # Parse and reformat data = json.loads(content) print("JSON is valid. Writing output file...") with open(output_file, 'w', encoding='utf-8') as f: json.dump(data, f, ensure_ascii=False, indent=2) print(f"Successfully fixed JSON and saved to {output_file}") except json.JSONDecodeError as e: print(f"Error: Invalid JSON: {str(e)}") # Get context around the error lines = content.split('\n') start = max(0, e.lineno - 3) end = min(len(lines), e.lineno + 3) context = '\n'.join(f"{i+1}: {line}" for i, line in enumerate(lines[start:end], start)) print("\nContext around error:") print(context)
if __name__ == '__main__': fix_json('dataset.json', 'dataset_fixed.json')
